package subarray_product_less_than_k

import (
	"testing"

	"github.com/stretchr/testify/assert"
)

type TestCaseDataSet struct {
	answer int
	k      int
	nums   []int
}

func BenchmarkNumSubarrayProductLessThanK(b *testing.B) {

	dataSet := getTestDataSet()

	for i := 0; i < b.N; i++ {
		for _, data := range dataSet {
			numSubarrayProductLessThanK(data.nums, data.k)
		}
	}
}

func TestNumSubarrayProductLessThanK(t *testing.T) {

	for _, data := range getTestDataSet() {
		assert.Equal(t, data.answer, numSubarrayProductLessThanK(data.nums, data.k))
	}
}

